import React, {useState, useEffect} from 'react';
import classes from './GlobalPerCountry.module.scss';
import axios from 'axios'

const GlobalPerCountry = () => {
    const [state, setState] = useState('');
    const getData = () => {
        const allAPI = 'https://corona.lmao.ninja/v2/countries?yesterday&cases';
        
        const fetchData = async () => {
            const data = await axios.get(allAPI);
            setState(data.data.cases);
            console.log(data.data[2]);
        }
        fetchData();
    }
    useEffect(() => {
        getData();
    }, [])
    return (
        <div className={classes.sheet__wrapper}>
            <table>
                <thead>
                    <td>Cases by</td>
                    <td>Country</td>
                </thead>
                <tbody>
                    <td> 1,829,903 </td>
                    <td>{state}</td>
                </tbody>
            </table>
        </div>
    )
}

export default GlobalPerCountry;
