import React, {useState, useEffect} from 'react';
import classes from './table.module.scss';
import axios from 'axios'

const Table = () => {
    let theAPIData;
    const [title, setTitle] = useState('Global Deaths')
    const [state, setState] = useState('');
    const getData = () => {
        const allAPI = 'https://corona.lmao.ninja/v2/all?yesterday';
        const fetchData = async () => {
            const data = await axios.get(allAPI);
            setState(data.data.deaths);
            theAPIData = data.data;
        }
        fetchData();
    }
    function recovery(){
        setTitle('Global recovery');
        setState(theAPIData.cases);
    }
    useEffect(() => {
        getData();
    }, [])
    return (
        <div className={classes.sheet__wrapper}>
            <table>
                <thead>
                    <td>{title}</td>
                </thead>
                <tbody>
                    <td>{state}</td>
                </tbody>
            </table>
            <button onClick={recovery}></button>
        </div>
    );
};
  
export default Table;
